import React from "react";
import 'rc-slider/assets/index.css';
import {useRef} from 'react';





const UserIngre = props =>  {

    //const [state,setState] = React.useState({like:"",nolike:"",similar:0})
    function handleChange(event){
        const value = event.target.value
        reference.current = {
            ...reference.current,
            [event.target.name] : value
        };

        props.onChange(reference.current)
    }
    const reference = useRef({"like":"","nolike":"","similar":0})
    return(
        <section className="user-select">
            <div className="want-ingred">
                <h3>Ingredients that I want to include in recipe</h3>
                <input className="like-input" name = "like" ref = {reference.current} onChange={handleChange}></input>
            </div>
            <div className="nowant-ingred">
                <h3>Ingredients that I don't like to include</h3>
                <input className="nolike-in" name = "nolike" ref = {reference.current} onChange={handleChange}></input>
            </div>
            <div className="CF">
                <h3>give me similar recommendation</h3>
                <div class="range">
                    <div class="field">
                        <input type="range" min="0" max="100" step="10" name = "similar"  ref = {reference.current} onChange={handleChange}className="slide"></input>
                    </div>
                </div>

            </div>

        </section>
    )
}


export default UserIngre;
