import React from "react";
import 'rc-slider/assets/index.css';
import {useState,useEffect} from 'react';
import { render } from "react-dom";




const UserIngre = props =>  {

    //const [state,setState] = React.useState({like:"",nolike:"",similar:0})
    function handleChange(event){
        const value = event.target.value
        setState({
            ...state,
            [event.target.name] : value
        });
        /*useEffect(()=> {
        fetch(`/recipe?like=${state.like};${state.nolike};${state.similar}`).then(
      res => res.json()
       ).then(
      ingredients => {
        setIngredients(ingredients)
        console.log(state)
      }
    )
   },[])*/
        props.onChange(state)
    }
    const [state,setState] = React.useState({like:"",nolike:"",similar:0})
    return(
        <section className="user-select">
            <div className="want-ingred">
                <h3>Ingredients that I want to include in recipe</h3>
                <input className="like-input" name = "like" value = {state.value} onChange={handleChange}></input>
            </div>
            <div className="nowant-ingred">
                <h3>Ingredients that I don't like to include</h3>
                <input className="nolike-in" name = "nolike" value = {state.nolike} onChange={handleChange}></input>
            </div>
            <div className="CF">
                <h3>give me similar recommendation</h3>
                <div class="range">
                    <div class="field">
                        <input type="range" min="0" max="100" step="10" name = "similar" value = {state.similar} onChange={handleChange}className="slide"></input>

                    </div>
                </div>

            </div>

        </section>
    )
}


export default UserIngre;
