import React, {useState} from 'react';
import UsingSteps from './components/UsingSteps';
import Input1 from './components/Input1';
import Checkedbox1 from './components/Checkedbox1';
import InputIngredient  from './components/InputIngredient';
import './APP.css';


const App = () => {
  const[ingredients, setIngredients] = useState(
  );

  const steps = [
    {id:'step1', text:'Entering the ingredients you have(no spaces between ingredients and seperate each with a comma)'},
    {id:'step2', text:'Check the filter for your recipes'},
    {id:'step3', text:'Clcik “Find recipe” & Get the links for recipes'},
  ];



  const ingredientHandler = ingredient => {
    setIngredients(ingredients.concat(ingredient));

  };



  return (
    <div>
      <h1 className = 'Title'>What Do You Wanna Have Today?</h1>
      <h2 className = 'Description'>Provide us the ingredients you have, Let us find you the Best Recipes</h2>
      <h3 className = 'Subtitle1'>Using Instruction</h3>
      <UsingSteps us ={steps} />
      <Checkedbox1 />
      <Input1 addGredient = {ingredientHandler}/>
      <InputIngredient inputs= {ingredients}/>



    </div>
  );
};

export default App;
