    //const [state,setState] = React.useState({like:"",nolike:"",similar:0})


    handleLike = (event) =>{
        const value = event.target.value;
        setState({
            ...state,
            [event.target.name]:value
        });
        console.log(state.like);
        console.log(state.nolike);
        console.log(state.similar);
    };
render(){
    return(
        <section className="user-select">
            <div className="want-ingred">
                <h3>Ingredients that I want to include in recipe</h3>
                <input className="like-input" name="like" value = {state.like} onChange={handleLike}></input>
            </div>
            <div className="nowant-ingred">
                <h3>Ingredients that I don't like to include</h3>
                <input className="nolike-in" name="nolike" value = {state.nolike} onChange={handleLike}></input>
            </div>
            <div className="CF">
                <h3>give me similar recommendation</h3>
                <div class="range">
                    <div class="field">
                        <input type="range" min="0" max="100" step="10" name = "similar" value = {state.similar} onChange={handleLike} className="slide"></input>

                    </div>
                </div>

            </div>

        </section>
    )
}

}
export default UserIngre;
