import React from 'react';

const Input1 = props=>{
    const SubmitHandler = event =>{
        event.preventDefault();
        const ingredient = {
            id: '1',
            text: 'INGREDIENT'
        };
        props.addGredient(ingredient);
    };
    return(
        <div>
            <h3 className = 'InputTitle'>Ingredients for your recipes</h3>
            <form className='P1' onSubmit={SubmitHandler}>
                <input type='parameter1'/>
                <button type='submit'>Find Recipe</button>
            </form>

        </div>


    )
}

export default Input1;
