  constructor(props){
    super(props);
    this.state={result:[]};
    this.dataFromInput = this.dataFromInput.bind(this);
  }



  /*
  this renders recipe recommendations to users

  */
  RenderIngre(prop){
    return(
      <div className="each-recipe">
        <h4 className='recipe-name'>{prop.name}</h4>
        <ul className="ing-list">
          {prop.inglist.map(ing=>(
            <li>{ing}</li>
          ))}
        </ul>
        <ol>
          {prop.steps.map(step=>(
            <li>{step}</li>
          ))}
        </ol>
      </div>
    )
  }



