import React, {useState,useEffect,useRef} from 'react';
import UsingSteps from './components/UsingSteps';
import Input1 from './components/Input1';
import Checkedbox1 from './components/Checkedbox1';
// import InputIngredient  from './components/InputIngredient';
import './APP.css';
import UserIngre from './components/UserIngre';




const App = () => {
  const[ingredients, setIngredients] = useState({});
  const[render,setRender] = useState("False")
  const state = useRef({like:"",nolike:"",similar:0})


  /*useEffect(()=> {
    fetch(`/recipe?like=${state.like};${state.nolike};${state.similar}`).then(
      res => res.json()
    ).then(
      ingredients => {
        setIngredients(ingredients)
        console.log(state)
      }
    )
  },[])*/
  const steps = [
    {id:'step1', text:'Entering the ingredients you have(no spaces between ingredients and seperate each with a comma)'},
    {id:'step2', text:'Check the filter for your recipes'},
    {id:'step3', text:'Click “Find recipe” & Get the links for recipes'},
  ];



  const ingredientHandler = ingredient => {
    setIngredients(ingredients.concat(ingredient));

  };

  /*
  this renders recipe recommendations to users
  */
  function RenderIngre(prop){
    return(
      <div className="each-recipe">
        <h4 className='recipe-name'>{prop.name}</h4>
        <ul className="ing-list">
          {prop.inglist.map(ing=>(
            <li>{ing}</li>
          ))}
        </ul>
        <ol>
          {prop.steps.map(step=>(
            <li>{step}</li>
          ))}
        </ol>
      </div>
    )
  }

  function handleClick() {
    setRender("True");
    fetch(`/recipe?like=${state.like};${state.nolike};${state.similar}`).then(
      res => res.json()
    ).then(
      ingredients => {
        setIngredients(ingredients)
        console.log(state)
      }
    );
  }

  useEffect(()=> {
    console.log(state)
  },[])

  return (
    <div> 
      <nav>
      <h1 className = 'Title'>What Do You Wanna Have Today?</h1>
      <div className = 'Subtitle1'>
      <h3>Using Instruction</h3>
      <UsingSteps us ={steps} />
      </div>
      </nav>
      <div>

      <UserIngre onChange={(value)=>state.current = value}/>
      <button onClick={handleClick} className="but">Explore</button>
      <div className="recipe-block"> 
      {(typeof ingredients.member == 'undefined')? (
      <p>Loading...</p>
    ):(
      render === "True" && ingredients.member.map((mem,i)=>(
        <RenderIngre name={mem.name}
        inglist = {mem.inglist}
        steps = {mem.step}
        />
      ))
    )}
    </div>

      {/* <Checkedbox1 />*/}
      {/* <InputIngredient inputs= {ingredients}/> */}



      </div>
    </div>
  );
};

export default App;
