            </div>
            <div className="CF">
                <h3>give me similar recommendation</h3>
                <div class="range">
                    <div class="field">
                        <input type="range" min="0" max="100" step="10" onChange={handleChange} className="slide"></input>

                    </div>
                </div>

            </div>

        </section>
    )
}
